import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import InputArea from "./InputArea2";
import Note from "./Note";

function App() {

    const [array, setArray] = React.useState([]);

    let arr = [{"title":"asdf"},{"title":"asdg"}]

    console.log(array,"adsfkdsjn")
    function addNote(newNote){
        setArray(prevNotes => {
            // Here we are holding the value of previous notes or calling the previous 
            // notes then we'll add it with new notes.
            return [...prevNotes, newNote];
        });
    }
    const [todos, setTodos] = React.useState([])
    const [getArr, setGetArr] = React.useState([])
    React.useEffect(() => {
        localStorage.setItem('todo', JSON.stringify(array))
        localStorage.setItem('asdf', JSON.stringify(arr))
        setTodos(...JSON.parse(localStorage.getItem('todo')))
        setGetArr(...JSON.parse(localStorage.getItem('asdf')))
    }, [array])
    
    console.log(typeof todos, typeof getArr)
    function deleteNote(id){
        setArray(prevNotes => {
            return prevNotes.filter((noteItem, index) => {
                return index!==id;
            })
        })
    }


    return (
        <div>
        <Header />
        <InputArea onAdd={addNote}/>
        {todos ? todos.map((noteItem, index) => {
            return( 
                <Note 
                key={index}
                id={index}
                title={noteItem.title} 
                content={noteItem.content} 
                onDelete={deleteNote} /> )
        }): <>hi</>}
        <Footer />
        </div>
    )
}

export default App;
